import axios from 'axios'


const COURSE_API_URL = 'http://localhost:8080/app'

class PeopleDataService {

    retrieveAllPeople() {
        //console.log('executed service')
        return axios.get(`/people`);
    }

    retrievePerson(id) {
        //console.log('executed service')
        return axios.get(`/people/${id}`);
    }

    deletePerson(id) {
        //console.log('executed service')
        return axios.delete(`/people/${id}`);
    }

    updatePerson(id, person) {
        //console.log('executed service')
        return axios.put(`/people/${id}`, person);
    }

    createPerson(person) {
        //console.log('executed service')
        return axios.post(`/people/`, person);
    }
}

export default new PeopleDataService()
